CREATE TABLE `data`.`resources` ( `id` INT NOT NULL AUTO_INCREMENT , `domain` TEXT NOT NULL , `login_url` TEXT NULL DEFAULT NULL , `login` TEXT NULL DEFAULT NULL , `password` TEXT NULL DEFAULT NULL , `url` TEXT NULL DEFAULT NULL , `accountId` INT NULL DEFAULT NULL , `createdAt` DATETIME NULL DEFAULT NULL , `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB ;